    @classmethod
    def calculateSmoothnessIndicator(cls, stencil: List[int], i: int, qArray: npt.NDArray) -> float:
        size = len(stencil)

        match size:
            case 2:
                B = cls.__B1
            case 3:
                B = cls.__B2
            case 4: 
                B = cls.__B3
            case 5:
                B = cls.__B4
            case 6:
                B = cls.__B5
            case 7:
                B = cls.__B6
            case 8:
                B = cls.__B7
            case 9:
                B = cls.__B8
            case 10:
                B = cls.__B9
        
        shifted_stencil = [idx - i for idx in stencil]
        stencil_number = size - shifted_stencil.index(0) - 1
        smoothness = qArray[stencil].T @ B[stencil_number] @ qArray[stencil]

        return smoothness